package com.brink.model;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class Plugin {    private static final Logger logger = LoggerFactory.getLogger(Plugin.class);    private String name;    private String type;    public Plugin() {    }    public Plugin(String fileName) {        this.type = extractFileExtension(fileName);        int lastDotIndex = fileName.lastIndexOf('.');        if (lastDotIndex != -1) {            this.name = fileName.substring(0, lastDotIndex);        } else {            this.name = fileName;        }    }    private String extractFileExtension(String fileName) {        int lastDotIndex = fileName.lastIndexOf('.');        if (lastDotIndex > 0 && lastDotIndex < fileName.length() - 1) {            String extension = fileName.substring(lastDotIndex + 1).toLowerCase();            if (extension.equals("dll")) {                return "VST2";            } else {                return "VST3";            }        }        return "";    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }}