package com.brink.model.adapters;import com.brink.model.FileData;import com.google.gson.*;import java.lang.reflect.Type;public class FileDataAdapter implements JsonDeserializer<FileData> {    @Override    public FileData deserialize(JsonElement json, Type typeOfT, JsonDeserializationContext context) throws JsonParseException {        JsonObject jsonObject = json.getAsJsonObject();        FileData fileData = new FileData();        fileData.setProjectName(jsonObject.get("projectName").getAsString());        return fileData;    }}