package com.brink.model.app;import com.brink.model.PluginFormat;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class Plugin {    private static final Logger logger = LoggerFactory.getLogger(Plugin.class);    private String name;    private PluginFormat format;    public Plugin() {    }    public Plugin(String fileName) {        this.format = PluginFormat.extractPluginFormat(fileName);        int lastDotIndex = fileName.lastIndexOf('.');        if (lastDotIndex != -1) {            this.name = fileName.substring(0, lastDotIndex);        } else {            this.name = fileName;        }    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public PluginFormat getFormat() {        return format;    }    public void setFormat(PluginFormat format) {        this.format = format;    }}