package com.brink.model.ui;import com.brink.model.ableton.AbletonDevice;import javafx.beans.property.SimpleBooleanProperty;import javafx.beans.property.SimpleStringProperty;import javafx.scene.control.TableColumn;import javafx.scene.control.TableView;import javafx.scene.layout.VBox;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.List;public class AbletonDeviceTableView extends VBox {    private static final Logger logger = LoggerFactory.getLogger(AbletonDeviceTableView.class);    public AbletonDeviceTableView(List<AbletonDevice> abletonDeviceList) {        setSpacing(12);        setStyle("-fx-padding: 10;");        TableView<AbletonDevice> deviceTable = createDeviceTable();        deviceTable.getItems().addAll(abletonDeviceList);        getChildren().add(deviceTable);    }    private static TableView<AbletonDevice> createDeviceTable() {        TableView<AbletonDevice> table = new TableView<>();        table.setColumnResizePolicy(TableView.CONSTRAINED_RESIZE_POLICY);        TableColumn<AbletonDevice, String> nameCol = new TableColumn<>("Name");        nameCol.setCellValueFactory(cellData -> new SimpleStringProperty(cellData.getValue().getName()));        TableColumn<AbletonDevice, Boolean> isNativeCol = new TableColumn<>("Native");        isNativeCol.setCellValueFactory(cellData -> new SimpleBooleanProperty(cellData.getValue().isNative()));        table.getColumns().addAll(nameCol, isNativeCol);        return table;    }}